import './TODOList.css';
import { useState } from 'react';
import closeIcon from './close.png';

function Inputs({ onAddToDoItem }) {
    const [value, setValue] = useState("");

    function onAdd() {
        if (value === "") {
            return;
        }

        onAddToDoItem(value);
        setValue("");
    }

    return (
        <div className="inputs">
            <h1 className="title">To Do List</h1>
            <input
                placeholder="Add your new TODO item..."
                value={value}
                onChange={(e) => setValue(e.target.value)}
                onKeyDown={(e) => { if (e.key === "Enter") onAdd();}} />
            <button
                className="add-btn"
                onClick={onAdd}>Add</button>
        </div>
    );
}

function ListItem({ todo, onToggleToDo, onDeleteToDo }) {
    function hangleToggleToDo() {
        onToggleToDo( todo.name );
    }

    function handleDeleteToDo() {
        onDeleteToDo(todo.name);
    }

    return (
        <li>
            <span
                style={{ textDecoration: todo.finished ? "line-through" : "none" }}
                onClick={hangleToggleToDo}
            >
                {todo.name}
            </span>
            <button
                className="close"
                onClick={handleDeleteToDo} >
                <img src={closeIcon} className="close-icon" />
            </button>
        </li>
    );
}

function List({ todos, onToggleToDo, onDeleteToDo }) {
    const items = [];

    todos.forEach(todo => {
        items.push(<ListItem
            key={todo.name}
            todo={todo}
            onToggleToDo={onToggleToDo}
            onDeleteToDo={onDeleteToDo}
        />);
    });

    return (
        <ul>{items}</ul>
    );
}

export default function TODOList() {
    const [todos, setTodos] = useState([{ name: "Item1", finished: false },
        { name: "Clean", finished: true },
        { name: "Write", finished: false },
        { name: "Finish", finished: false },
    ]);

    function handleAddToDoItem(name1) {
        let nextTodos = todos.slice();
        nextTodos.push({ name:name1, finished:false });
        setTodos(nextTodos);
    }

    function handleToggleToDo(name1) {
        let nextTodos = todos.slice();
        let item = nextTodos.find((e) => e.name === name1 );
        item.finished = !item.finished;
        setTodos(nextTodos);
    }

    function handleDeleteToDo(name1) {
        let nextTodos = todos.slice();
        nextTodos.filter((e) => e.name === name1);
        setTodos(nextTodos);
    }

    return (
        <div className="whole-list">
            <Inputs
                onAddToDoItem={(name) => handleAddToDoItem(name)}
            />
            <List
                todos={todos}
                onToggleToDo={(name) => handleToggleToDo(name)}
                onDeleteToDo={(name) => handleDeleteToDo(name)}
            />
        </div>
    );
}
