import './TODOList.css';
import { useState } from 'react';
import closeIcon from './close.png';

function Inputs({ onAddToDoItem }) {
    const [value, setValue] = useState("");

    function onAdd() {
        if (value === "") {
            return;
        }

        onAddToDoItem(value);
        setValue("");
    }

    return (
        <div className="inputs">
            <h1 className="title">To Do List</h1>
            <input
                placeholder="Add your new TODO item..."
                value={value}
                onChange={(e) => setValue(e.target.value)}
                onKeyDown={(e) => { if (e.key === "Enter") onAdd();}} />
            <button
                className="add-btn"
                onClick={onAdd}>Add</button>
        </div>
    );
}

function ListItem({ todo, onToDoToggle }) {
    function hangleToDoToggle() {
        onToDoToggle( todo.name );
    }

    return (
        <li onClick={hangleToDoToggle}>
            <span
                style={{ textDecoration: todo.finished ? "line-through" : "none" }}>
                {todo.name}
            </span>
            <button className="close"><img src={closeIcon} className="close-icon" /></button>
        </li>
    );
}

function List({ todos, onToDoToggle }) {
    const items = [];

    todos.forEach(todo => {
        items.push(<ListItem
            key={todo.name}
            todo={todo}
            onToDoToggle={onToDoToggle} />);
    });

    return (
        <ul>{items}</ul>
    );
}

export default function TODOList() {
    const [todos, setTodos] = useState([{ name: "Item1", finished: false },
        { name: "Clean", finished: true },
        { name: "Write", finished: false },
        { name: "Finish", finished: false },
    ]);

    function handleAddToDoItem(name1) {
        let nextTodos = todos.slice();
        nextTodos.push({ name:name1, finished:false });
        setTodos(nextTodos);
    }

    function handleToDoToggle(name1) {
        let nextTodos = todos.slice();
        let item = nextTodos.findIndex((e) => e.name === name1 );
        item.finished = !item.finished;
        setTodos(nextTodos);
    }

    return (
        <div className="whole-list">
            <Inputs
                onAddToDoItem={(name) => handleAddToDoItem(name)} />
            <List
                todos={todos}
                onToDoToggle={(name) => handleToDoToggle(name)} />
        </div>
    );
}
