import './TODOList.css';
import { useState } from 'react';
import closeIcon from './close.png';

function Inputs({ onAddToDoItem }) {
    const [value, setValue] = useState('');

    function onAdd() {
        onAddToDoItem(value);
    }

    return (
        <div className="inputs">
            <h1 className="title">To Do List</h1>
            <input
                placeholder="Add your new TODO item..."
                value={value}
                onChange={setValue} />
            <button
                className="add-btn"
                onClick={onAdd}>Add</button>
        </div>
    );// + enter
}

function ListItem({ todo }) {
    return (
        <li>
            <div>
                {todo.name}
                <button className="close"><img src={closeIcon} className="close-icon" /></button>
            </div>
        </li>
    );
}

function List({todos}) {
    const items = [];

    todos.forEach(todo => {
        items.push(<ListItem todo={todo} />);
    });

    return (
        <ul>{items}</ul>
    );
}

export default function TODOList() {
    const [todos, setTodos] = useState([{ name: "Item1", finished: false },
        { name: "Clean", finished: true },
        { name: "Write", finished: false },
        { name: "Finish", finished: false },
    ]);

    function handleAddToDoItem({ name1 }) {
        let nextTodos = todos.slice();
        nextTodos.push({ name:{ name1 }, finished:false });
        setTodos(nextTodos);
    }

    return (
        <div className="whole-list">
            <Inputs onAddToDoItem={(name) => handleAddToDoItem(name)} />
            <List
                todos={todos} />
        </div>
    );
}
