import './App.css';
import { useState } from 'react';

function UserInputStock() {

    return (
        <>
            <div>
                <input></input>
            </div>
            <div>
                <input type="checkbox" id="stocked"></input>
                <label htmlFor="stocked">Only show products in stock</label>
            </div>
        </>
    );
}

function ProductCategoryRow({ category }) {
    return (
        <tr>
            <th colSpan="2">
                {category}
            </th>
        </tr>
    );
}

function ProductRow({ product }) {
    const name = product.stocked ? product.name :
        <span color="red">{product.name}</span>;

    return (
        <tr>
            <td>{name}</td>
            <td>{product.price}</td>;
        </tr>
    );
}

function ProductLists({products}) {
    let rows = [];
    let prevCategory = null;

    for (let product in products) {
        if (product.category != prevCategory) {
            rows.push(<ProductCategoryRow category={product.category} key={product.category} />);
            prevCategory = product.category;
        }

        rows.push(<ProductRow product={product} key={product.name}/>);
    }

    return (
        <table>
            <thead>
                <tr>
                    <th>Name</th>
                    <th>Price</th>
                </tr>
            </thead>
            <tbody>{ rows }</tbody>
        </table>
    );
}

export default function Shop() {
    const listOfProducts = [
        { category: "Fruits", price: "$1", stocked: true, name: "Apple" },
        { category: "Fruits", price: "$1", stocked: true, name: "Dragonfruit" },
        { category: "Fruits", price: "$2", stocked: false, name: "Passionfruit" },
        { category: "Vegetables", price: "$2", stocked: true, name: "Spinach" },
        { category: "Vegetables", price: "$4", stocked: false, name: "Pumpkin" },
        { category: "Vegetables", price: "$1", stocked: true, name: "Peas" }
    ];

    return (
        <>
            <UserInputStock />
            <ProductLists />
        </>
    );
}
